import React, { useState } from "react";
import "./compounding.css";
import Dermatology from "./images/dermatology.png";
import HairLoss from "./images/hairLoss.jpeg";
import Dental from "./images/dental.jpeg";
import HormoneTherapy from "./images/hormoneTherapy.jpg";
import PainManagement from "./images/painManagement.jpeg";
import Veterinary from "./images/Veterinary.jpeg"
const Compounding: React.FC = () => {
  const [selectedProduct, setSelectedProduct] = useState<string | null>(null);

  const openPopup = (product: string) => {
    setSelectedProduct(product);
  };

  const closePopup = () => {
    setSelectedProduct(null);
  };

  const getPopupContent = () => {
    switch (selectedProduct) {
      case "Dermatology":
        return (
          <div>
            <h2>Dermatology</h2>
            <ul>
              <li>Aesthetic Treatments: Products used to enhance the appearance of the skin.</li>
              <li>Lightening Agents: Products aimed at reducing hyperpigmentation, to help even skin tone and brighten complexions.</li>
              <li>Chemical Peels: These treatments involve applying a chemical solution to exfoliate the skin, improving texture and reducing signs of aging, acne, and scarring.</li>
              <li>Acne Treatments: This encompasses topical and oral medications, including retinoids, benzoyl peroxide, and antibiotics.</li>
              <li>Keratosis and Wart Treatments: Addressing conditions like actinic keratosis, treatments remove or treat warts or precancerous growths.</li>
            </ul>
          </div>
        );
      case "Hair Loss":
        return (
          <div>
            <h2>Hair Loss</h2>
            <ul>
              <li>Capsules: Containing specified ingredients that support hair health and growth.</li>
              <li>Hair Solutions: Topical treatments, designed to stimulate hair growth and prevent further hair loss.</li>
            </ul>
          </div>
        );
      case "Dental":
        return (
          <div>
            <h2>Dental</h2>
            <ul>
              <li>Topical Anesthetics: Gels and ointments applied directly to the gums to numb the area prior to procedures, providing immediate relief from pain.</li>
              <li>Bone Binder: Used in dental implant procedures to regenerate bone density and support dental structures.</li>
              <li>Specialized Numbing Gels: Formulations designed specific to each patientâ€™s needs and dental procedures that require more targeted pain relief.</li>
            </ul>
          </div>
        );
      case "Hormone Therapy":
        return (
          <div>
            <h2>Hormone Therapy</h2>
            <ul>
              <li>Men's and Women's Health: Hormone replacement therapies for addressing hormonal imbalances, menopause symptoms in women, and testosterone deficiencies in men.</li>
              <li>Sexual Enhancement: Treatments aimed at improving libido and sexual function, including specialized topical medications.</li>
            </ul>
          </div>
        );
      case "Pain Management":
        return (
          <div>
            <h2>Pain Management</h2>
            <ul>
              <li>Numbing Creams and Ointments: Topical formulations that provide localized pain relief for conditions such as arthritis, neuropathy, or minor injuries.</li>
              <li>Gels and BLT (Benzocaine, Lidocaine, Tetracaine): Combinations of local anesthetics used for more effective pain relief in various settings.</li>
            </ul>
          </div>
        );
      case "Veterinary":
        return (
          <div>
            <h2>Veterinary</h2>
            <ul>
              <li>Feline and Canine Medications: A range of pharmaceuticals specifically designed to treat health issues in pets, including anti-inflammatories, antibiotics, and pain management solutions.</li>
            </ul>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="compounding-container">
      <div className="title">Compounding</div>
      <div className="product-grid">
        <div className="product-item">
          <img src={Dermatology} alt="Dermatology" />
          <p>Dermatology</p>
          <button onClick={() => openPopup("Dermatology")}>LEARN MORE</button>
        </div>
        <div className="product-item">
          <img src={HairLoss} alt="Hair Loss" />
          <p>Hair Loss</p>
          <button onClick={() => openPopup("Hair Loss")}>LEARN MORE</button>
        </div>
        <div className="product-item">
          <img src={Dental} alt="Dental" />
          <p>Dental</p>
          <button onClick={() => openPopup("Dental")}>LEARN MORE</button>
        </div>
        <div className="product-item">
          <img src={HormoneTherapy} alt="Hormone Therapy" />
          <p>Hormone Therapy</p>
          <button onClick={() => openPopup("Hormone Therapy")}>LEARN MORE</button>
        </div>
        <div className="product-item">
          <img src={PainManagement} alt="Pain Management" />
          <p>Pain Management</p>
          <button onClick={() => openPopup("Pain Management")}>LEARN MORE</button>
        </div>
        <div className="product-item">
          <img src="veterinary.png" alt="Veterinary" />
          <p>Veterinary</p>
          <button onClick={() => openPopup("Veterinary")}>LEARN MORE</button>
        </div>
      </div>

      {selectedProduct && (
        <div className="popup-overlay" onClick={closePopup}>
          <div className="popup" onClick={(e) => e.stopPropagation()}>
            {getPopupContent()}
            <button onClick={closePopup}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Compounding;
